import React from 'react';
import './Resume.css';
import Card from './Card';

const expData = [
    {
        id: 1,
        category: 'education',
        icon: 'icon-graduation',
        year: '2015 - 2019',
        title: 'Bachelors of Technology in Computer Science and Engineering',
        desc: (
            <span>
                University of Sri Jayewardenepura <br /> GPA - 3.54
            </span>
        ),
    },
    {
        id: 2,
        category: 'education',
        icon: 'icon-graduation',
        year: '2023 - 2024',
        title: 'Diploma in Software Engineering',
        desc: (
            <span>
                Developers Stack Academy <br /> Tvec Approved
            </span>
        ),
    },
    {
        id: 3,
        category: 'education',
        icon: 'icon-graduation',
        year: '2017 - 2019',
        title: 'GCE (AL) in Technology Stream',
        desc: (
            <span>
                Taxila Central College Horana <br /> 2As and B pass
            </span>
        ),
    },
    {
        id: 4,
        category: 'experience',
        icon: 'icon-briefcase',
        year: ' Now',
        title: 'SE Intern @ Verdentra',
        desc: 'I’m equipped with full-stack web development skills and willing learn new technologies',
    },
    {
        id: 5,
        category: 'experience',
        icon: 'icon-briefcase',
        year: '2023 - 2024',
        title: 'Web Developer (Wordpress)',
        desc: (
            <span>
                Find X pvt LTD <br /> Worked remotely, Designed and built user-friendly
                websites, and landing pages using WordPress and Elementor.
            </span>
        ),
    },
    {
        id: 6,
        category: 'experience',
        icon: 'icon-briefcase',
        year: '2019 - 2020',
        title: 'ICT Tutor',
        desc: 'Conduct ICT classes for students and help them to prepare for the examination.',
    },
];

const Resume = () => {
    return (
        <section className="resume container section" id="resume">
            <h2 className="section__title">Experience</h2>

            <div className="resume__container grid">
                <div className="timeline grid">
                    {expData.map(
                        (exp, index) =>
                            exp.category === 'education' && (
                                <Card
                                    key={index}
                                    icon={exp.icon}
                                    title={exp.title}
                                    year={exp.year}
                                    desc={exp.desc}
                                />
                            )
                    )}
                </div>

                <div className="timeline grid">
                    {expData.map(
                        (exp, index) =>
                            exp.category === 'experience' && (
                                <Card
                                    key={index}
                                    icon={exp.icon}
                                    title={exp.title}
                                    year={exp.year}
                                    desc={exp.desc}
                                />
                            )
                    )}
                </div>
            </div>
        </section>
    );
};

export default Resume;
