import React from 'react';
import './Resume.css';
import Card from './Card';

const expData = [
    {
        id: 1,
        category: 'education',
        icon: 'icon-graduation',
        year: '2015 - 2019',
        title: 'Bachelors of Technology in Computer Science and Engineering',
        desc: (
            <span>
                Vellore Institute of Technology, Chennai
            </span>
        ),
    },
    {
        id: 2,
        category: 'education',
        icon: 'icon-graduation',
        year: '2021-2023',
        title: 'Masters in Computer Science',
        desc: (
            <span>
                Rutgers University, New Brunswick
            </span>
        ),
    },
    {
        id: 3,
        category: 'experience',
        icon: 'icon-briefcase',
        year: ' 2019-2020',
        title: 'Data Analyst @ GVPL Technologies',
        desc: (
            <span>
                Spearheaded data analysis projects within GVPL Technologies.
                Devised data-driven staffing strategies resulting in a 15% reduction in
                project costs, leveraging offshore technology support to scale department
                resources according to project requirements.
                Stack: Python,SQL,Tableau,AWS,Git
            </span>
        )
    },
    {
        id: 5,
        category: 'experience',
        icon: 'icon-briefcase',
        year: '2020 - 2021',
        title: 'Data Engineer @ Phenom People',
        desc: (
            <span>
                Built data pipelines by developing ETL scripts using Python and SQL, scheduling using Apache Airflow, au-
                tomating data validations and report generations, reducing man hours by 20% following CI/CD practices.
                Reduced load time by 10 times by migrating to Amazon Redshift from PostgreSQL,optimizing read and write
                performance through data modeling and design of schemas.
                Lowered customer onboarding time by 40% by building an automated job scheduler, in collaboration with cross
                functional team to retrieve files from SFTP Server as soon as created.
                Stack: Python,SQL,Airflow,AWS
                S3,IAM,Git.
            </span>
        ),
    },
    {
        id: 6,
        category: 'experience',
        icon: 'icon-briefcase',
        year: '2022 - 2022',
        title: 'Research Assitant, Rutgers University',
        desc: (
            <span>
                Implemented and replicated state-of-the-art Active Learning models for object detection on a custom dataset,
                resulting in a 59.83% reduction in labeling costs for object detection datasets.
                â€“ Achieved a MAP score of 28.6% on the second cycle, by mapping the dataset to meet model requirements and
                retraining the models to compare the performance of the novel pipeline against the baseline. Stack:PyTorch,Linux.
            </span>
        )
    },
];

const Resume = () => {
    return (
        <section className="resume container section" id="resume">
            <h2 className="section__title">Experience</h2>

            <div className="resume__container grid">
                <div className="timeline grid">
                    {expData.map(
                        (exp, index) =>
                            exp.category === 'education' && (
                                <Card
                                    key={index}
                                    icon={exp.icon}
                                    title={exp.title}
                                    year={exp.year}
                                    desc={exp.desc}
                                />
                            )
                    )}
                </div>

                <div className="timeline grid">
                    {expData.map(
                        (exp, index) =>
                            exp.category === 'experience' && (
                                <Card
                                    key={index}
                                    icon={exp.icon}
                                    title={exp.title}
                                    year={exp.year}
                                    desc={exp.desc}
                                />
                            )
                    )}
                </div>
            </div>
        </section>
    );
};

export default Resume;
